import vueLottiePlayer from "./vue-lottie-player.vue";

function install(Vue) {
  if (install.installed) return;
  install.installed = true;
  Vue.component("vLottiePlayer", vueLottiePlayer);
}

const plugin = {
  install
};

let GlobalVue = null;
if (typeof window !== "undefined") {
  GlobalVue = window.Vue;
} else if (typeof global !== "undefined") {
  GlobalVue = global.vue;
}
if (GlobalVue) {
  GlobalVue.use(plugin);
}

vueLottiePlayer.install = install;

export default vueLottiePlayer;
